sysauto_script.script:
var rightNow = new GlideDateTime();
var rightNowPlus1 = new GlideDateTime();
rightNowPlus1 = gs.hoursAgo(-24);
gs.info ("rightNow : "+rightNow+"  rightNowPlus1  "+rightNowPlus1);
var dueNITasks = new GlideRecord('x_58872_needit_needit_task');
dueNITasks.addQuery('due_date', '>=', rightNow);
dueNITasks.addQuery('due_date', '<=', rightNowPlus1);
dueNITasks.addQuery('state', '!=', 3);

dueNITasks.query();
// Write a log message for each overdue NeedIt Task Record
while (dueNITasks.next()) {
    gs.info("Due NeedIt Task record = " + dueNITasks.number+"  due today "+dueNITasks.due_date);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',dueNITasks,dueNITasks.number,gs.getUserName());
}
